import React, { Component } from 'react';

class Users extends Component {
state = {
Users: [
{  age: 20 },
],
Users1: [
{  age: 40 },

],
Users2: [
{  age: 40 },

],
title: "User list"
}

makeMeYounger = () => {
const newState = this.state.Users.map((User) => {
const tempUser = User;
tempUser.age += 10;
return tempUser;
})
console.log(newState);
this.setState({ newState });
}
makeMeYounger1 = () => {
if(this.state.Users1.age>0){
const sub = this.state.Users1.map((User1) => {
const tempUser1 = User1;

tempUser1.age -=10; 

return tempUser1;

})
}
console.log(sub);
this.setState({ sub });
}
makeMeYounger2 = () => {
const newState = this.state.Users2.map((User2) => {
const tempUser2 = User2;
tempUser2.age *= 10;
return tempUser2;
})
console.log(newState);
this.setState({ newState});
}
render() {
return (
<div>
<div>
<button onClick={this.makeMeYounger}>add</button>
<br />

<h1>{this.state.title}</h1>
{
this.state.Users.map((User) => (
<div>User Age={User.age} name={User.name}</div>
))
}
</div>




<div>
<button onClick={this.makeMeYounger1}>sub</button>
<br />

<h1>{this.state.title}</h1>
{
this.state.Users1.map((User1) => (
<div>User1 Age={User1.age} name={User1.name}</div>
))
}
</div>
<div>
<button onClick={this.makeMeYounger2}>Multiply</button>
<br />

<h1>{this.state.title}</h1>
{
this.state.Users2.map((User2) => (
<div>User2 Age={User2.age} name={User2.name}</div>
))
}
</div>
</div>
)
}
}



export default Users;
